import React, { Component } from 'react';
import '../assets/css/App.css';
import '../assets/css/bootstrap.css';


class User extends Component {

  state = {
    pictures: []
  }


  // ------ OLD API CALL  ------ //

  componentDidMount() {
    fetch('https://randomuser.me/api/?nat=us')
      .then(results => { return results.json() })
      .then(data => {
        let pictures = data.results.map((person) => {
          return (
            <div>
              <div class="container">
                <div class="row">
                  <div class="col-md-9">
                    <img src={person.picture.large} />
                    <div class="data">
                      <h1>{person.name.title} {person.name.first} {person.name.last}</h1>
                      <p>{person.email}</p>
                      <p>{person.dob.date}</p>
                      <p>{person.dob.age} years old</p>
                      <p>{person.phone}</p>
                      </div>
                  </div>
                  <div class="col-md-3">
                  var FA = require('react-fontawesome')
 
React.render(<FA name="rocket" />, document.body)
                  </div>
                </div>
              </div>

            </div>
          )
        })
        this.setState({ pictures: pictures });
      })
  }


  render() {

    // ------  THE RETURN BLOCK  ------ //

    return (
      <div className="App">

        {this.state.pictures}

      </div>
    )
  }
}

export default User;
