import React, { Component } from 'react';
import '../assets/css/App.css';
import '../assets/css/bootstrap.css';


class User extends Component {

  state = {
    pictures: []
  }

  getNewPerson(){
    this.componentDidMount();
  }

  componentDidMount() {
    fetch('https://randomuser.me/api/?nat=us')
      .then(results => { return results.json() })
      .then(data => {
        let pictures = data.results.map((person) => {
          return (
            this.state
          )
        })
        this.setState({ pictures: pictures });
      })
      .catch(function(err) {
        console.log('Fetch Error:', err);
      });
  }

  render() {

    return (
      <div className="App">

  <div class="container">
                <div class="row">
                  <div class="col-md-9">
                    <img src={person.picture.large} />
                    <div class="data">
                      <h1>{person.name.title} {person.name.first} {person.name.last}</h1>
                      <p>{person.email}</p>
                      <p>{person.dob.date}</p>
                      <p>{person.dob.age} years old</p>
                      <p>{person.phone}</p>
                      </div>
                  </div>
                  <div class="col-md-3">
                      <a class="btn btn-default" onClick={this.componentDidMount}>Nuevo</a>
                  </div>
                </div>
              </div>

        

      </div>
    )
  }
}

export default User;
