import React, { Component } from 'react';
import Moment from 'react-moment';
import './App.css';


class User extends Component {

    state = {
      pictures: []
  }


// ------ OLD API CALL  ------ //

  componentDidMount() {
    fetch('https://randomuser.me/api/?nat=us')
    .then(results => { return results.json()})
    .then(data => { let pictures = data.results.map((person) => {
        return (
          <div>
            <img src={person.picture.large}/> 
            <p>{person.name.title} {person.name.first} {person.name.last}</p>
            <p>{person.email}</p>
            <p>{person.dob.date}</p>
            <p>{person.dob.age} years old</p>
            <p>{person.phone}</p>
          </div>
          )
      })
    this.setState({ pictures: pictures });
  })
}


render() {

// ------  THE RETURN BLOCK  ------ //

return (
  <div className="App">


{/* HEADER */}

        <header className="App-header">
          <h1>Random Person</h1>
        </header>


{/* COMPONENTS */}

         {this.state.pictures}     
  
  </div>
  )
 }
}

export default User;
