import React, { Component } from 'react';
import '../assets/css/App.css';
import '../assets/css/bootstrap.css';



class User extends Component {

  constructor(props){
    super(props);
    this.state = {
      pictures: []
    }
    this.url = "https://randomuser.me/api/";
  }


  /*refreshPage() {
    window.location.reload(false);
  }*/

  componentDidMount(url) {
    fetch(this.url)
      .then(results => { return results.json() })
      .then(data => {
        let pictures = data.results.map((person) => {
          this.setState({
            url: "https://randomuser.me/api/",
            image: person.picture.large,
            name: person.name.title,
            email: person.email,
            birth: person.dob.date,
            age: person.dob.age,
            phone: person.phone
          })
        })
        this.setState({ pictures: pictures });
      })
  }


  render() {
    return (
      <div className="App">
        <div className="container">
                <div className="row">
                  <div className="col-md-9 n_card">
                    <img src={person.state.image} />
                    <div className="data">
                      <h1>{person.status.name}</h1>
                      <p>{person.status.email}</p>
                      <p>{person.status.birth}</p>
                      <p>{person.status.age} years old</p>
                      <p>{person.status.phone}</p>
                      </div>
                  </div>
                  <div className="col-md-3">
                  <button className="btn btn-light" onClick={() => {this.url = "https://randomuser.me/api/"}}>Random</button><br/>
                  <button className="btn btn-light" onClick={() => {this.url = "https://randomuser.me/api/?gender=female"}}>♀</button><br/>
                  <button className="btn btn-light" onClick={() => {this.url = "https://randomuser.me/api/?gender=male"}}>♂</button>
                  </div>
                </div>
              </div>
      </div>
    )
  }
}

export default User;
