import React, { Component } from 'react';
import '../assets/css/App.css';
import '../assets/css/bootstrap.css';



class User extends Component {

  state = {
    pictures: []
  }

  /*refreshPage() {
    window.location.reload(false);
  }*/

  componentDidMount(url) {
    fetch(url)
      .then(results => { return results.json() })
      .then(data => {
        let pictures = data.results.map((person) => {
          url = "https://randomuser.me/api/";
          return (
            <div>
              <div className="container">
                <div className="row">
                  <div className="col-md-9 n_card">
                    <img src={person.picture.large} />
                    <div className="data">
                      <h1>{person.name.title} {person.name.first} {person.name.last}</h1>
                      <p>{person.email}</p>
                      <p>{person.dob.date}</p>
                      <p>{person.dob.age} years old</p>
                      <p>{person.phone}</p>
                      </div>
                  </div>
                  <div className="col-md-3">
                  <button className="btn btn-light" onClick={this.componentDidMount(url)}>Random</button><br/>
                  <button className="btn btn-light" onClick={this.componentDidMount(url+"?gender=female")}>♀</button><br/>
                  <button className="btn btn-light" onClick={this.componentDidMount(url+"?gender=male")}>♂</button>
                  </div>
                </div>
              </div>

            </div>
          )
        })
        this.setState({ pictures: pictures });
      })
  }


  render() {
    return (
      <div className="App">

        {this.state.pictures}

      </div>
    )
  }
}

export default User;
