import React, { Component } from 'react';
import '../assets/css/App.css';
import '../assets/css/bootstrap.css';


class User extends Component {

  state = {
    card: []
  }

  componentDidMount() {
    fetch('https://randomuser.me/api/?nat=us')
      .then(results => { return results.json() })
      .then(data => {
        let pictures = data.results.map((person) => {
          return (
            <div>
              

            </div>
          )
        })
        this.setState({ pictures: pictures });
      })
  }


  render() {
    return (
      <div className="App">

        {this.state.card}

        <div className="container">
                <div className="row">
                  <div className="col-md-9">
                    <img src={person.picture.large} />
                    <div className="data">
                      <h1>{person.name.title} {person.name.first} {person.name.last}</h1>
                      <p>{person.email}</p>
                      <p>{person.dob.date}</p>
                      <p>{person.dob.age} years old</p>
                      <p>{person.phone}</p>
                      </div>
                  </div>
                  <div className="col-md-3">
                  <a className="btn btn-primary">Random</a><br/>
                  <a className="btn btn-danger">Mujer</a><br/>
                  <a className="btn btn-success">Hombre</a>
                  </div>
                </div>
              </div>

      </div>
    )
  }
}

export default User;
